import React, {useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deletePost, likePost} from '../features/posts/postsSlice.js'
import {Box, Card, CardActions, CardContent, CardMedia, Button, Typography, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle} from '@mui/material'
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt'
import DeleteIcon from '@mui/icons-material/Delete'
import MoreHorizIcon from '@mui/icons-material/MoreHoriz'
import {formatDistanceToNow} from 'date-fns'

function Post({post, setCurrentPostId}) {
  const dispatch = useDispatch()
  const loggedInUser = useSelector(state => state.user.currentUser)

  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false)
  
  return (
    <Card
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        borderRadius: 4,
        height: '100%',
        position: 'relative',
      }}
    >
      <CardMedia 
        image={post.imgBase64} 
        title={post.title}
        sx={{
          pt: '50%',
          bgcolor: 'rgba(0, 0, 0, 0.5)',
          backgroundBlendMode: 'darken',
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          top: '5%',
          left: '5%',
          color: 'white',
        }}
      >
        <Typography
          variant='h6'
        >
          {post.creator}
        </Typography>
        <Typography
          variant='body2'
        >
          {formatDistanceToNow(new Date(post.createdAt))}
        </Typography>
      </Box>

      <Box
        sx={{
          position: 'absolute',
          top: '5%',
          right: '5%',
          color: 'white'
        }}
      >
        <Button
          size='small'
          onClick={() => setCurrentPostId(post._id)}
          sx={{
            color: 'white'
          }}
        >
          <MoreHorizIcon />
        </Button>
      </Box>

      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          m: 2,
        }}
      >
        <Typography
          variant='body2'
        >
          {post.tags.map((tag) => `#${tag} `)}
        </Typography>
      </Box>

      <Typography 
        gutterBottom 
        variant="h5"
        sx={{
          px: 2
        }}
      >
        {post.title}
      </Typography>

      <CardContent>
        <Typography
          variant='body2'
          gutterBottom
          color='textSecondary'
        >
          {post.message}
        </Typography>
      </CardContent>

      <CardActions
        sx={{
          px: 2,
          pt: 1,
          display: 'flex',
          justifyContent: 'space-between',
        }}
      >
        <Button
          size='small'
          onClick={() => {
            dispatch(likePost(post._id, loggedInUser._id))
          }}
        >
          <ThumbUpAltIcon 
            fontSize='small'
            sx={{
              mr: 1
            }}
          />
          Like
          {/* {` (${post.likes})`}   */}
        </Button>

        <Button
          size='small'
          onClick={() => {
            setDeleteDialogOpen(true)
          }}
        >
          <DeleteIcon 
            fontSize='small'
            sx={{
              mr: 1
            }}
          />
          Delete
        </Button>

        <Dialog
          open={deleteDialogOpen}
          onClose={() => setDeleteDialogOpen(false)}
          aria-labelledby="delete-button-dialog-box"
          aria-describedby='prompts the user with a confirmation dialog for deleting a post'
        >
          <DialogTitle>
            {"Are you sure you want to delete this post?"}
          </DialogTitle>

          <DialogContent>
            <DialogContentText>
              Once the post is deleted it will be removed from PetSocial forever!
            </DialogContentText>
          </DialogContent>

          <DialogActions>
            <Button
              onClick={() => setDeleteDialogOpen(false)}
            >
              Nevermind
            </Button>
            <Button
              onClick={() => {
                setDeleteDialogOpen(false)
                dispatch(deletePost(post._id))
              }}
            >
              Delete the post
            </Button>
          </DialogActions>
        </Dialog>
      </CardActions>
    </Card>
  )
}

export default Post
