import React from 'react'
import {Box, Card, CardActions, CardContent, CardMedia, Button, Typography} from '@mui/material'
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt'
import DeleteIcon from '@mui/icons-material/Delete'
import MoreHorizIcon from '@mui/icons-material/MoreHoriz'
import {formatDistanceToNow} from 'date-fns'

function Post({post}) {
  return (
    <Card
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        borderRadius: 2,
        height: '100%',
        position: 'relative'
      }}
    >
      <CardMedia 
        image={post.imgBase64} 
        title={post.title}
        sx={{
          pt: '50%',
          bgcolor: 'rgba(0, 0, 0, 0.5)',
          backgroundBlendMode: 'darken',
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          top: '10%',
          left: '10%',
          color: 'white',
        }}
      >
        <Typography
          variant='h6'
        >
          {post.creator}
        </Typography>
        <Typography
          variant='body2'
        >
          {formatDistanceToNow(new Date(post.createdAt))}
        </Typography>
      </Box>

      <Box
        sx={{
          position: 'absolute',
          top: 2,
          right: 2,
        }}
      >
        <Button
          size='small'
          onClick={() => console.log('cheese')}
        >
          <MoreHorizIcon 
            sx={{
              color: 'white'
            }}
          />
        </Button>
      </Box>

      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          m: 2,
        }}
      >
        <Typography
          variant='body2'
        >
          {post.tags.map((tag) => `#${tag} `)}
        </Typography>
      </Box>

      <Typography 
        gutterBottom 
        variant="h5"
        sx={{
          px: 2
        }}
      >
        {post.title}
      </Typography>

      <CardContent>
        <Typography
          variant='h5'
          gutterBottom
        >
          {post.message}
        </Typography>
      </CardContent>

      <CardActions
        sx={{
          px: 2,
          pt: 1,
          display: 'flex',
          justifyContent: 'space-between',
        }}
      >
        <Button
          size='small'
          onClick={() => console.log('chicken nuggets')}
        >
          <ThumbUpAltIcon 
            fontSize='small'
          />
          Like 
          {post.likes}  
        </Button>

        <Button
          size='small'
          onClick={() => console.log('chicken nuggets')}
        >
          <DeleteIcon 
            fontSize='small'
          />
          Delete
        </Button>
      </CardActions>
    </Card>
  )
}

export default Post
