import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import postsService from "./postsService"

const initialState = {
    posts: [],
    isError: false,
    isSuccess: false,
    isLoading: false,
    message: ''
}

export const getPosts = createAsyncThunk('posts/getPosts', async (_, thunkAPI) => {
    // const response = await api.getPosts()
    // return response.data
    try {
        return await postsService.getPosts()
    } catch (error) {
        (error.response && error.response.data && error.resonse.data.message)
        || error.message
        || error.toString()

        return thunkAPI.rejectWithValue(message)
    }
})

export const createPost = createAsyncThunk('posts/createPost', async (_, thunkAPI) => {
    try {
        return await postsService.createPost()
    } catch (error) {
        (error.response && error.response.data && error.resonse.data.message)
        || error.message
        || error.toString()

        return thunkAPI.rejectWithValue(message)
    }
})

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        // reducers
    },
    extraReducers(builder) {
        builder
            .addCase(getPosts.pending, (state, action) => {
                state.isLoading = true
            })
            .addCase(getPosts.fulfilled, (state, action) => {
                state.isLoading = false
                state.isSuccess = true
                state.posts = action.payload
            })
            .addCase(getPosts.rejected, (state, action) => {
                state.isLoading = false
                state.isError = true
                state.message = action.payload
            })
    }
})

export default postsSlice.reducer
