import { Avatar, Container, Grid, Paper, TextField, Typography, Box } from '@mui/material'
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import React from 'react'

function Auth() {
    const isSignUp = false

    const handleSubmit = () => {
        console.log('jelly')
    }

    const handleChange = () => {
        console.log('jam')
    }
    
    return (
        <Container maxWidth="xs">
            <Paper 
                elevation={3}
                sx={{
                    mt: 2,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    p: 2,
                }}
            >
                <Avatar
                    sx={{
                        m: 1,
                    }}
                >
                    <LockOutlinedIcon 
                        color='secondary'
                    />
                </Avatar>

                <Typography variant="h5">{isSignUp ? "Sign Up" : "Sign In"}</Typography>

                <form onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                        {isSignUp && (
                            <>
                                <Grid xs={6} md={12}>
                                    <TextField
                                        name="firstName" label="First Name" handleChange={handleChange}
                                        autoFocus
                                        xs={6}
                                        variant="outlined"
                                        required
                                        fullWidth
                                    />
                                </Grid>
                                <Grid xs={6} md={12}>
                                    <TextField
                                        name="lastName"
                                        label="Last Name"
                                        handleChange={handleChange} autoFocus
                                        xs={6}
                                        variant="outlined"
                                        required
                                        fullWidth
                                    />
                                </Grid>
                            </>
                        )}
                    </Grid>
                </form>
            </Paper>
        </Container>
    )
}

export default Auth
