import React, { useState } from 'react'
import { useHistory, useLocation, NavLink } from 'react-router-dom'
import Posts from './Posts.jsx'
import Form from './Form.jsx'
import {
	Grid,
	Paper,
	Pagination,
	PaginationItem,
	AppBar,
	TextField,
	ListItem,
	Chip,
	Autocomplete,
} from '@mui/material'

function Home() {
	const [currentPostId, setCurrentPostId] = useState(null)
	const [searchTags, setSearchTags] = useState([
		{
			key: 0,
			label: 'Angular',
		},
		{
			key: 1,
			label: 'jQuery',
		},
		{
			key: 2,
			label: 'Polymer',
		},
		{
			key: 3,
			label: 'React',
		},
		{
			key: 4,
			label: 'Vue.js',
		},
	])

	const handleRemoveTag = (e, tagToDelete) => {
		e.preventDefault()
		setSearchTags((tags) =>
			tags.filter((tag) => tag.key !== tagToDelete.key)
		)
	}

	return (
		<Grid
			container
			justifyContent='space-between'
			alignItems='stretch'
			spacing={3}
		>
			<Grid item xs={12} sm={6} md={9}>
				<Posts setCurrentPostId={setCurrentPostId} />
			</Grid>

			<Grid item xs={12} sm={6} md={3}>
				<AppBar
					position='static'
					color='inherit'
					sx={{
						borderRadius: 1,
						mb: 2,
						display: 'flex',
						p: 2,
					}}
				>
					<TextField name='search' label='Search Posts' fullWidth />
				</AppBar>

				<Paper
					sx={{
						display: 'flex',
						justifyContent: 'center',
						flexWrap: 'wrap',
						listStyle: 'none',
						p: 0.5,
						m: 0,
					}}
					// component='ul'
				>
					{/* {searchTags.map((tagData) => {
							return (
								<ListItem key={tagData.key}>
									<Chip
										label={tagData.label}
										size='small'
										onDelete={(e) =>
											handleRemoveTag(e, tagData)
										}
									/>
								</ListItem>
							)
						})} */}
					<Autocomplete
						multiple
						id='tagsSearch'
						options={[]}
						defaultValue={[]}
						freeSolo
						onChange={(e, value) => {
							setSearchTags((state) => value)
						}}
						renderTags={(value, getTagProps) =>
							value.map((option, idx) => (
								<Chip
									variant='outlined'
									label={option}
									{...getTagProps({ idx })}
								/>
							))
						}
						renderInput={(params) => {
							return (
								<TextField
									{...params}
									fullWidth
									variant='filled'
									label='tagInputField'
									placeholder='Dogs'
								/>
							)
						}}
					/>
				</Paper>

				<Paper
					elevation={6}
					sx={{
						mb: 2,
						p: 2,
					}}
				>
					<Pagination
						count={5}
						page={1}
						color='secondary'
						renderItem={(item) => (
							<PaginationItem
								component={NavLink}
								to='/auth'
								{...item}
							/>
						)}
					/>
				</Paper>
				<Form
					currentPostId={currentPostId}
					setCurrentPostId={setCurrentPostId}
				/>
			</Grid>
		</Grid>
	)
}

export default Home
