import { Avatar, Button, Box, Container, Paper, TextField, Typography } from '@mui/material'
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import React from 'react'

function Auth() {
    const isSignUp = true

    const handleSubmit = () => {
        console.log('jelly')
    }

    const handleChange = () => {
        console.log('jam')
    }
    
    return (
        <Container maxWidth="xs" sx={{
            mt: 8,
        }}>
            <Paper 
                elevation={3}
                sx={{
                    my: 2,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    p: 2
                }}
            >
                <Avatar
                    sx={{
                        my: 1,
                        backgroundColor: 'red'
                    }}
                >
                    <LockOutlinedIcon />
                </Avatar>

                <Typography 
                    variant="h5"
                    sx={{
                        mb: 2
                    }}
                >
                    {isSignUp ? "Sign Up" : "Sign In"}
                </Typography>

                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center'
                    }}
                >
                    <form onSubmit={handleSubmit}>
                        <Container
                            container={true}
                            spacing={2}
                            sx={{
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'center',
                                alignItems: 'center'
                            }}
                        >
                            {isSignUp && (
                                <>
                                        <TextField
                                            name="firstName" label="First Name" onChange={handleChange}
                                            autoFocus
                                            xs={6}
                                            variant="outlined"
                                            required
                                            fullWidth
                                        />
                                        <TextField
                                            name="lastName"
                                            label="Last Name"
                                            onChange={handleChange}
                                            xs={6}
                                            variant="outlined"
                                            required
                                            fullWidth
                                            sx={{
                                                my: 2
                                            }}
                                        />
                                </>
                            )}
                                <TextField
                                    name="email"
                                    label="Email Address"
                                    type='email'
                                    onChange={handleChange}
                                    autoFocus={isSignUp}
                                    xs={6}
                                    variant="outlined"
                                    required
                                    fullWidth
                                    sx={{
                                        mb: 2
                                    }}
                                />
                                <TextField
                                    name="password"
                                    label="Password"
                                    type="password"
                                    onChange={handleChange}
                                    xs={6}
                                    variant="outlined"
                                    required
                                    fullWidth
                                />
                            <Button
                                type="submit"
                                fullWidth
                                variant="contained"
                                sx={{
                                    m: 2,
                                }}
                            >
                                {isSignUp ? "Sign Up" : "Sign In"}
                            </Button>
                        </Container>
                    </form>
                </Box>
            </Paper>
        </Container>
    )
}

export default Auth
