import React, {useState} from 'react'
import {TextField, Button, Typography, Paper, Box} from '@mui/material'
import FileBase from 'react-file-base64'

function Form() {
  const [postData, setPostData] = useState({
    creator: '',
    title: '',
    message: '',
    tags: '',
    imgBase64: '',
  })

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log('bleh')
  }

  const handleClear = () => {
    console.log('jed')
  }
  
  return (
    <Paper sx={{
      m: 2,
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center'
    }}>
      <form 
        autoComplete='off' 
        noValidate 
        onSubmit={handleSubmit}
      >
        <Typography 
          variant='h6'
          align='center'
          sx={{
            my: 2,
          }}
        >
          Creating a Memory
        </Typography>
        <TextField 
          name='creator' 
          variant='outlined' 
          label="Creator" 
          fullWidth
          value={postData.creator}
          onChange={(e) => setPostData({
            ...postData,
            creator: e.target.value
          })}
        />
        <TextField 
          name='title' 
          variant='outlined' 
          label="Title" 
          fullWidth
          value={postData.creator}
          onChange={(e) => setPostData({
            ...postData,
            title: e.target.value
          })}
        />
        <TextField 
          name='message' 
          variant='outlined' 
          label="Message" 
          fullWidth
          value={postData.creator}
          onChange={(e) => setPostData({
            ...postData,
            message: e.target.value
          })}
        />
        <TextField 
          name='tags' 
          variant='outlined' 
          label="Tags" 
          fullWidth
          value={postData.creator}
          onChange={(e) => setPostData({
            ...postData,
            tags: e.target.value
          })}
        />

        <Box
          sx={{
            width: '90%',
            my: 1,
          }}
        >
          <FileBase 
            type='file'
            multiple={false}
            onDone={({base64}) => setPostData({
              ...postData,
              imgBase64: base64
            })}
          />
        </Box>

        <Button 
          variant='contained' 
          size='large' 
          type='submit' 
          fullWidth
        >
          Submit
        </Button>

        <Button 
          variant='contained'
          color='secondary' 
          size='small' 
          onClick={handleClear} 
          fullWidth
        >
          Clear
        </Button>
      </form>
    </Paper>
  )
}

export default Form
