import React from 'react'
import { useDispatch } from 'react-redux'
import { deletePost, likePost } from '../../../features/posts/postsSlice'
import {Box, Card, CardActions, CardContent, CardMedia, Button, Typography} from '@mui/material'
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt'
import DeleteIcon from '@mui/icons-material/Delete'
import MoreHorizIcon from '@mui/icons-material/MoreHoriz'
import {formatDistanceToNow} from 'date-fns'

function Post({post, setCurrentPostId}) {
  const dispatch = useDispatch()
  
  return (
    <Card
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        borderRadius: 2,
        height: '100%',
        position: 'relative'
      }}
    >
      <CardMedia 
        image={post.imgBase64} 
        title={post.title}
        sx={{
          pt: '50%',
          bgcolor: 'rgba(0, 0, 0, 0.5)',
          backgroundBlendMode: 'darken',
        }}
      />

      <Box
        sx={{
          position: 'absolute',
          top: '5%',
          left: '5%',
          color: 'white',
        }}
      >
        <Typography
          variant='h6'
        >
          {post.creator}
        </Typography>
        <Typography
          variant='body2'
        >
          {formatDistanceToNow(new Date(post.createdAt))}
        </Typography>
      </Box>

      <Box
        sx={{
          position: 'absolute',
          top: '5%',
          right: '5%',
          color: 'white'
        }}
      >
        <Button
          size='small'
          onClick={() => setCurrentPostId(post._id)}
          sx={{
            color: 'white'
          }}
        >
          <MoreHorizIcon />
        </Button>
      </Box>

      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          m: 2,
        }}
      >
        <Typography
          variant='body2'
        >
          {post.tags.map((tag) => `#${tag}`).join(', ')}
        </Typography>
      </Box>

      <Typography 
        gutterBottom 
        variant="h5"
        sx={{
          px: 2
        }}
      >
        {post.title}
      </Typography>

      <CardContent>
        <Typography
          variant='h5'
          gutterBottom
        >
          {post.message}
        </Typography>
      </CardContent>

      <CardActions
        sx={{
          px: 2,
          pt: 1,
          display: 'flex',
          justifyContent: 'space-between',
        }}
      >
        <Button
          size='small'
          onClick={() => dispatch(likePost(post._id))}
        >
          <ThumbUpAltIcon 
            fontSize='small'
            sx={{
              mr: 1
            }}
          />
          Like
          {` (${post.likes})`}  
        </Button>

        <Button
          size='small'
          onClick={() => dispatch(deletePost(post._id))}
        >
          <DeleteIcon 
            fontSize='small'
            sx={{
              mr: 1
            }}
          />
          Delete
        </Button>
      </CardActions>
    </Card>
  )
}

export default Post
