import React from 'react'
import { Link } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import { logoutUser } from '../features/users/userSlice'
import { useAuthStatus } from '../hooks/useAuthStatus'
import {Box, AppBar, Typography, Avatar, Button} from '@mui/material'
import dogLogo from '../assets/dogLogo.png'
import FaceIcon from '@mui/icons-material/Face'
import { deepOrange } from '@mui/material/colors'

function Navbar() {
    const dispatch = useDispatch()
    const {loggedIn, checkingStatus} = useAuthStatus()

    const firstName = useSelector(state => (
        loggedIn && 
            state.user.user.name
                .slice(0, 1)
                .toUpperCase()
                .concat(state.user.user.name.slice(1))
    ))
    
    
    const firstLetter = useSelector(state => loggedIn && state.user.user.name[0].toUpperCase())
    
    return (
        <AppBar 
            position="static" 
            color="inherit"
            sx={{
                borderRadius: 4,
                py: 1,
                px: 4,
                my: 3,
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'space-between',
                alignItems: 'center',
            }}
        >
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row'
                }}
            >
                <Typography
                    variant="h2"
                    align="center"
                    component={Link}
                    to="/"
                >
                    PetSocial
                </Typography>
                
                <Box
                    sx={{
                    mx: 5,
                    }}
                >
                    <img
                        src={dogLogo}
                        alt="logo"
                        height="75"
                        // Image source: https://www.vecteezy.com/vector-art/5162048-cartoon-dog-walking-on-white-background "Dog Vectors by Vecteezy" submitted by user tigatelu
                    />
                </Box>
            </Box>

            {loggedIn ? (
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        width: '30%'
                    }}
                >
                    <Avatar
                        src={FaceIcon}
                        sx={{
                            bgcolor: deepOrange[500],
                        }}
                    >
                        {firstLetter}
                    </Avatar>
                    <Typography
                        sx={{
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center',
                            fontWeight: 'bold',
                        }}
                    >
                        {firstName}
                    </Typography>
                    <Button 
                        variant="contained" 
                        color="secondary"
                        onClick={() => dispatch(logoutUser())}
                    >
                        Logout
                    </Button>
                </Box>
            ) : (
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'row',
                        px: 2,
                    }}
                >
                    <Button component={Link} to="/auth" variant="contained">Sign In</Button>
                </Box>
            )}
        </AppBar>
    )
}

export default Navbar
