import { Avatar, Button, Container, Grid, Paper, TextField, Typography } from '@mui/material'
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import React from 'react'

function Auth() {
    const isSignUp = false

    const handleSubmit = () => {
        console.log('jelly')
    }

    const handleChange = () => {
        console.log('jam')
    }
    
    return (
        <Container maxWidth="xs">
            <Paper 
                elevation={3}
                sx={{
                    mt: 2,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    p: 2,
                }}
            >
                <Avatar
                    sx={{
                        m: 1,
                        backgroundColor: 'red'
                    }}
                >
                    <LockOutlinedIcon />
                </Avatar>

                <Typography variant="h5">{isSignUp ? "Sign Up" : "Sign In"}</Typography>

                <form onSubmit={handleSubmit}>
                    <Grid 
                        container 
                        spacing={2}
                        sx={{
                            display: 'flex',
                            justifyContent: "center"
                        }}
                    >
                        {isSignUp && (
                            <>
                                <Grid xs={6} md={12} item>
                                    <TextField
                                        name="firstName" label="First Name" handleChange={handleChange}
                                        autoFocus
                                        xs={6}
                                        variant="outlined"
                                        required
                                        fullWidth
                                    />
                                </Grid>
                                <Grid xs={6} md={12} item>
                                    <TextField
                                        name="lastName"
                                        label="Last Name"
                                        handleChange={handleChange}
                                        xs={6}
                                        variant="outlined"
                                        required
                                        fullWidth
                                    />
                                </Grid>
                            </>
                        )}

                        <Grid xs={6} md={12} item>
                            <TextField
                                name="email" 
                                label="Email Address" 
                                type='email'
                                handleChange={handleChange}
                                autoFocus={isSignUp}
                                xs={6}
                                variant="outlined"
                                required
                                fullWidth
                            />
                        </Grid>
                        <Grid xs={6} md={12} item>
                            <TextField
                                name="password"
                                label="Password"
                                type="password"
                                handleChange={handleChange}
                                xs={6}
                                variant="outlined"
                                required
                                fullWidth
                            />
                        </Grid>

                        <Button 
                            type="submit" 
                            fullWidth 
                            variant="contained"
                            sx={{
                                m: 2,
                            }}
                        >
                            {isSignUp ? "Sign Up" : "Sign In"}
                        </Button>
                    </Grid>
                </form>
            </Paper>
        </Container>
    )
}

export default Auth
