import React, {useState, useEffect} from 'react'
import { useDispatch } from 'react-redux'
import {getPosts} from '../src/features/posts/postsSlice.js'
import {Box, Container, AppBar, Typography, Grid} from '@mui/material'
import dogLogo from './assets/dogLogo.png'
import Posts from './components/Posts/Posts.jsx'
import Form from './components/Form/Form.jsx'

function App() {
  const [currentPostId, setCurrentPostId] = useState(null)

  const dispatch = useDispatch()

  //  load posts if there are any
  useEffect(() => {
    dispatch(getPosts())
  }, [dispatch])

  return (
    <Container maxWidth="lg">
      <AppBar 
        position="static" 
        color="inherit"
        sx={{
          borderRadius: 4,
          p: 2,
          display: 'flex',
          flexDirection: 'row',
          // justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Typography variant="h2" align="center">
          PetSocial
        </Typography>
        <Box sx={{
          mx: 5,
        }}>
          <img
            src={dogLogo}
            alt="logo"
            height="75"
            // Image source: https://www.vecteezy.com/vector-art/5162048-cartoon-dog-walking-on-white-background "Dog Vectors by Vecteezy" submitted by user tigatelu
          />
        </Box>
      </AppBar>

      <Grid 
        container 
        justifyContent="space-between"
        alignItems="stretch"
        spacing={3}
      >
        <Grid
          item 
          xs={12}
          sm={7}
        >
          <Posts setCurrentPostId={setCurrentPostId}/>
        </Grid>
        <Grid
          item 
          xs={12}
          sm={4}
        >
          <Form currentPostId={currentPostId} setCurrentPostId={setCurrentPostId}/>
        </Grid>
      </Grid>
    </Container>
  )
}

export default App;
