import React from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'
import { useAuthStatus } from '../hooks/useAuthStatus'
import {Box, AppBar, Typography, Avatar, Button} from '@mui/material'
import dogLogo from '../assets/dogLogo.png'
import FaceIcon from '@mui/icons-material/Face'
import { deepOrange } from '@mui/material/colors'

function Navbar() {
    const {loggedIn, checkingStatus} = useAuthStatus()

    const firstName = useSelector(state => (
        loggedIn && state.user.user.name.slice[0, 1] 
    ))
    
    
    const firstLetter = useSelector(state => loggedIn && state.user.user.name[0].toUpperCase())
    
    return (
        <AppBar 
            position="static" 
            color="inherit"
            sx={{
                borderRadius: 4,
                p: 2,
                my: 3,
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'space-between',
                alignItems: 'center',
            }}
        >
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row'
                }}
            >
                <Typography
                    variant="h2"
                    align="center"
                    component={Link}
                    to="/"
                >
                    PetSocial
                </Typography>
                
                <Box
                    sx={{
                    mx: 5,
                    }}
                >
                    <img
                        src={dogLogo}
                        alt="logo"
                        height="75"
                        // Image source: https://www.vecteezy.com/vector-art/5162048-cartoon-dog-walking-on-white-background "Dog Vectors by Vecteezy" submitted by user tigatelu
                    />
                </Box>
            </Box>

            {loggedIn ? (
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'row'
                    }}
                >
                    <Avatar
                        src={FaceIcon}
                        sx={{
                            bgcolor: deepOrange[500],
                            mx: 1
                        }}
                    >
                        {firstLetter}
                    </Avatar>
                    <Typography>{firstName}</Typography>
                    <Button variant="contained" color="secondary">Logout</Button>
                </Box>
            ) : (
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'row',
                        px: 2,
                    }}
                >
                    <Button component={Link} to="/auth" variant="contained">Sign In</Button>
                </Box>
            )}
        </AppBar>
    )
}

export default Navbar
